<script>
import { mapGetters } from 'vuex'
import { saveAs } from 'file-saver'

export default {
    data: () => ({
        modules: [
            { key: 'special-decorations', name: '特装审图', icon: '/dist/images/icons/mapCad.png', path: '/special-decoration' },
            { key: 'costs', name: '固定费用缴纳', icon: '/dist/images/icons/mapCost.png', path: '/fixed-cost' },
            { key: 'rents', name: '水电家具租赁', icon: '/dist/images/icons/mapLease.png', path: '/my-orders?rent=1' },
            { key: 'deposits', name: '申请退还押金', icon: '/dist/images/icons/mapDeposit.png', path: '/refund' },
            { key: 'insurance-policies', name: '保险购买', icon: '/dist/images/icons/mapInsure.png', path: '/insure' },
            { key: 'invoices', name: '发票开具', icon: '/dist/images/icons/mapInvoice.png', path: '/invoice' },
        ],
        search: '',
        exhibitions: []
    }),

    computed: {
        ...mapGetters(['exhibition']),

        items() {
            return this.modules.filter(module => {
                return this.exhibition.modules.includes(module.key)
            })
        }
    },

    methods: {

        downloadFile(id) {
            axios
                .request({
                    method: 'post',
                    url: `exhibitions/${this.exhibition.id}/download/${id}`,
                    responseType: 'blob'
                })
                .then(response => saveAs(response.data))
        }
    }
}
</script>
